function getDeriByID(root::OpNode, id::Int64)
    if root.id == id
        return root.deri;
    end
    if root.sons === nothing
        return ;
    end
    for i in root.sons
        r = getDeriByID(i, id);
        if r!==nothing
            return r;
        end
    end
end

